pragma solidity >=0.6.0 <0.8.0;

import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC721/IERC721.sol";
import "openzeppelin-solidity/contracts/token/ERC1155/IERC1155.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/access/Ownable.sol";
import "./DKGcontract.sol";

/*

UAI/UALs essentially are:

- DID/DID URLs native to the OriginTrail DKG
- uniquely identify physical or digital assets (DID subjects)
- have one or more "owners" (DID controllers)

The specification for UAI/UALs is currently WIP.

*/

contract UAIRegistry is Ownable {

	enum AssetType{
		Native,
		ERC721,
		ERC1155
	}

	using SafeMath for uint256;
	IERC20 token;
	DKGcontract AssertionRegistry;
	address AssertionRegistryAddress;

	struct AssetRecord{
		uint256 creationTimestamp;
		bytes32 alsoKnownAs; // alternative ID, as defined in DID-core spec, PoC address + tokenURI
		uint8 _type;
		address controller; // tbd 
		bytes32 stateCommitHash;
	}

	mapping(bytes32 => AssetRecord) public assetRecords;
	mapping(bytes32 => bool) public UAIstatus; 
	mapping(bytes32 => uint256) public assetStake; 


	// events
	event UAIRegistryInit(address indexed assertionRegistryAddress);
	event UAICreated(bytes32 indexed UAI, bytes32 indexed stateCommitHash);
	event UAIUpdatedState(bytes32 indexed UAI, bytes32 indexed stateCommitHash );
	event TokensDepositedToAsset(bytes32 indexed UAI, uint256 indexed amount, address indexed depositor);

	constructor(address tokenAddress, address assertionRegistryAddress)  public {
		require(tokenAddress!=address(0));
		require(assertionRegistryAddress!=address(0));

		token = IERC20(tokenAddress);
		AssertionRegistryAddress = assertionRegistryAddress;
		AssertionRegistry = DKGcontract(assertionRegistryAddress);
		emit UAIRegistryInit(assertionRegistryAddress);
	}

	function registerAsset(bytes32 UAI, uint8 _type, bytes32 alsoKnownAs, bytes32 stateCommitHash, bytes32 rootHash, uint256 tokenAmount) public returns (bytes32){

		require(UAI!=0, "UAI cannot be zero");
		require(_type < 3, "_type allowed values: 0,1,2 (WIP)");
		require(alsoKnownAs!=0, "alsoKnownAs cannot be zero");
		require(stateCommitHash!=0, "stateCommitHash cannot be zero");
		require(UAIstatus[UAI] == false ); // should not be possible to register existing asset again

		//bytes32 UAI = createUAI(_type,alsoKnownAs);

		// depositTokens(tokenAmount, UAI); // todo

		AssertionRegistry.createAssertionRecord(stateCommitHash, rootHash, 1, 1); // todo: update when DKGcontract is updated
		assetRecords[UAI].stateCommitHash = stateCommitHash;
		assetRecords[UAI]._type = _type;
		assetRecords[UAI].alsoKnownAs = alsoKnownAs;
		assetRecords[UAI].controller = msg.sender; // should update with AssetControllerProxy contract
		assetRecords[UAI].creationTimestamp = block.timestamp;
		UAIstatus[UAI] = true;

		emit UAICreated(UAI, stateCommitHash);

		return UAI;
	}	


	function updateAssetState(bytes32 UAI, bytes32 newStateCommitHash, bytes32 rootHash) public returns (bytes32) {

		require(UAI!=0, "UAI cannot be zero");
		require(UAIstatus[UAI] == true, "Asset must exist" );
		require(newStateCommitHash!=0, "newStateCommitHash cannot be zero");
		require(isOwner(UAI,msg.sender));

		AssertionRegistry.createAssertionRecord(newStateCommitHash, rootHash, 1, 1); // UPDATE
		assetRecords[UAI].stateCommitHash =  newStateCommitHash;
		// todo: store old commits somewhere

		emit UAIUpdatedState(UAI, newStateCommitHash);

		return UAI;
	}


	// getters
	function getAssetStateCommitHash(bytes32 UAI) public view returns(bytes32 stateCommitHash){
		return assetRecords[UAI].stateCommitHash;
	}

	function getAssetController(bytes32 UAI) public view returns(address controller){
		return assetRecords[UAI].controller;
	}

	function getAssetAKA(bytes32 UAI) public view returns(bytes32 alsoKnownAs){
		return assetRecords[UAI].alsoKnownAs;
	}

	function getAssetCreationTimestamp(bytes32 UAI) public view returns(uint256 creationTimestamp){
		return assetRecords[UAI].creationTimestamp;
	}

	function getAssertionRegistry() public view returns(address assertionRegistry){
		return AssertionRegistryAddress;
	}

	// internal


	/**
     * @dev Should determine type of ``contractAddress``, if possible, returning enum AssetType
     */
     function determineType(address contractAddress, bytes32 id) internal returns (uint8){
		return 0; // todo: implement detection of contract type
	}

	/**
     * @dev Returns bool checking if ``owner`` is controller of ``UAI``
     */
     function isOwner(bytes32 UAI, address owner) internal returns (bool) {
     	return owner == assetRecords[UAI].controller;
     }

	/**
     * @dev Deposits tokens in ``amount`` to specific ``UAI``
     */
     function depositTokens(uint256 amount, bytes32 UAI) public {

     	require(amount>0, "Amount cannot be zero");
     	require(token.allowance(msg.sender, address(this)) >= amount, "Sender allowance must be equal to or higher than chosen amount");
     	require(token.balanceOf(msg.sender) >= amount, "Sender balance must be equal to or higher than chosen amount!");

     	bool transaction_result = token.transferFrom(msg.sender, address(this), amount);
     	require(transaction_result, "Token transaction execution failed!");

		// if (stake[msg.sender] == 0){
		// 	participant_indexes[msg.sender] = participants.length;
		// 	participants.push(msg.sender);
		// }

		assetStake[UAI] = assetStake[UAI].add(amount);
		emit TokensDepositedToAsset(UAI, amount,msg.sender);

	}	


}
